import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar.jsx';
import CTA from '../components/CTA.jsx';

export default function Portfolio() {
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [selectedService, setSelectedService] = useState('All');
  const [sortBy, setSortBy] = useState('Most Viewed');
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // API Configuration
  const API_BASE_URL = 'https://www.sarrdeh-tech.com/wp-json/wp/v2';
  const API_ENDPOINTS = {
    posts: `${API_BASE_URL}/posts?_embed&per_page=20`,
    media: `${API_BASE_URL}/media?per_page=50`,
    categories: `${API_BASE_URL}/categories`,
    customPosts: `${API_BASE_URL}/projects?_embed&per_page=20`
  };

  // CORS Proxy URLs (try multiple options)
  const CORS_PROXIES = [
    'http://localhost:3001/api/', // Local proxy server (development)
    'https://cors-anywhere.herokuapp.com/',
    'https://api.allorigins.win/raw?url=',
    'https://corsproxy.io/?',
    '' // Direct access (no proxy)
  ];

  // Sample data as fallback
  const sampleProjects = [
    {
      id: 1,
      title: "Emergency Medical Training Center (EMTC)",
      category: "Education Training",
      service: "Sarrdeh Care",
      location: "Kingdom of Saudi Arabia",
      description: "Since 2002, the Emergency Medicine Center has been one of the leading medical training centers in Saudi Arabia. In 2023, the center began its digital transformation in collaboration with Sarrdeh, where we developed a new identity, an educational platform (LMS), a student app, and a website for courses and registration, in addition to managing social media.",
      image: "/images/emtc-logo-01_result.webp",
      tags: ["Sarrdeh Care", "Sarrdeh Creative", "Sarrdeh Development", "Sarrdeh Marketing"],
      views: 1250,
      date: "2023-01-15T00:00:00",
      modified: "2023-12-01T00:00:00",
      link: "https://www.sarrdeh-tech.com/en/projects/emtc",
      slug: "emtc"
    },
    {
      id: 2,
      title: "A-Z for Medical Books and Supplies",
      category: "Books Publishing",
      service: "Sarrdeh Development",
      location: "Malaysia",
      description: "The project was established in 2017 as a medical books and supplies store in Malaysia, with worldwide shipping. The company aims to support medical education and practice for students and professionals, both in Malaysia and globally. Driven by a passion for providing high-quality medical resources, A-Z contributes to empowering the next generation of doctors and healthcare professionals.",
      image: "/images/A-Z-logo-01_result.webp",
      tags: ["Sarrdeh Care", "Sarrdeh Creative", "Sarrdeh Development", "Sarrdeh Marketing"],
      views: 980,
      date: "2017-06-20T00:00:00",
      modified: "2023-11-15T00:00:00",
      link: "https://www.sarrdeh-tech.com/en/projects/a-z-medical",
      slug: "a-z-medical"
    },
    {
      id: 3,
      title: "Team 1 for Healthcare and Emergency Medical Transport",
      category: "Education Training",
      service: "Sarrdeh Creative",
      location: "Kingdom of Saudi Arabia",
      description: "Since 2022, Team 1 has emerged as a leading provider of rapid ambulance services in Saudi Arabia, through a model built on technology and organization. At Sarda, we developed a fully integrated digital experience that included the website, a dispatch system for medical teams, a client-facing app, as well as visual identity, content, and comprehensive digital marketing.",
      image: "/images/DD-logo-01_result.webp",
      tags: ["Sarrdeh Care", "Sarrdeh Creative", "Sarrdeh Development", "Sarrdeh Marketing"],
      views: 1100,
      date: "2022-03-10T00:00:00",
      modified: "2023-10-20T00:00:00",
      link: "https://www.sarrdeh-tech.com/en/projects/team-1",
      slug: "team-1"
    },
    {
      id: 4,
      title: "Eat Wise For Healthy Food",
      category: "Food Beverages",
      service: "Sarrdeh Creative",
      location: "Kingdom of Saudi Arabia",
      description: "Eat Wise is a Saudi application specializing in offering healthy, balanced, and delicious meals tailored to suit the modern lifestyle. The concept was launched with the aim of providing a safe and convenient daily option for individuals seeking nutritious meals without compromising on flavor or presentation.",
      image: "/images/Eat-wise-logo-01_result.webp",
      tags: ["Sarrdeh Creative"],
      views: 850,
      date: "2023-08-15T00:00:00",
      modified: "2023-09-30T00:00:00",
      link: "https://www.sarrdeh-tech.com/en/projects/eat-wise",
      slug: "eat-wise"
    },
    {
      id: 5,
      title: "Oppolia Online for Designing and Manufacturing Integrated Home Solutions",
      category: "Design Decor",
      service: "Sarrdeh Development",
      location: "Kingdom of Saudi Arabia",
      description: "Oppolia Online is a digital initiative launched by Oppolia – Saudi Arabia in 2024, enabling customers to request a fully remote kitchen design session. At Sarda, we designed and developed the platform with a smart, user-friendly interface that offers a seamless experience — from filling out the form to payment and follow-up — serving as a comprehensive digital solution.",
      image: "/images/oppolia-logo-01_result-300x200.webp",
      tags: ["Sarrdeh Care", "Sarrdeh Creative", "Sarrdeh Development"],
      views: 720,
      date: "2024-01-20T00:00:00",
      modified: "2024-01-25T00:00:00",
      link: "https://www.sarrdeh-tech.com/en/projects/oppolia",
      slug: "oppolia"
    }
  ];

  const categories = [
    'All', 'Agriculture', 'Beauty Skincare', 'Books Publishing', 'Consulting Investments',
    'Design Decor', 'Education Training', 'Events Conferences', 'Fashion Art', 'Finance Insurance',
    'Fitness Wellness', 'Food Beverages', 'Fragrances', 'Healthcare Medical Services',
    'Hospitality Tourism', 'IOT', 'Marketing Agency', 'Nonprofit-Charity Organizations',
    'Real Estate Housing Services', 'Technology Tech Solutions'
  ];

  const services = ['All', 'Sarrdeh Care', 'Sarrdeh Creative', 'Sarrdeh Development', 'Sarrdeh Marketing'];
  const sortOptions = ['Most Viewed', 'Newest', 'Oldest'];

  // Service color mapping
  const getServiceColor = (service) => {
    switch (service) {
      case 'Sarrdeh Marketing':
        return '#F32735'; // Red
      case 'Sarrdeh Creative':
        return '#FFD100'; // Yellow
      case 'Sarrdeh Development':
        return '#57B6B2'; // Teal blue
      case 'Sarrdeh Care':
        return '#1E6390'; // Dark blue
      default:
        return '#57B6B2'; // Default teal
    }
  };

  // Extract image URL from WordPress API response
  const extractImageUrl = (post) => {
    // Check for featured media in embedded data
    if (post._embedded && post._embedded['wp:featuredmedia'] && post._embedded['wp:featuredmedia'][0]) {
      const media = post._embedded['wp:featuredmedia'][0];
      
      // Try different image sizes in order of preference
      if (media.media_details && media.media_details.sizes) {
        const sizes = media.media_details.sizes;
        return sizes.large?.source_url || 
               sizes.medium?.source_url || 
               sizes.thumbnail?.source_url || 
               media.source_url;
      }
      
      return media.source_url;
    }
    
    // Check for featured_media ID and try to construct URL
    if (post.featured_media) {
      return `${API_BASE_URL}/media/${post.featured_media}`;
    }
    
    // Extract image from content if no featured media
    const content = post.content?.rendered || '';
    const imgMatch = content.match(/<img[^>]+src="([^"]+)"/);
    if (imgMatch) {
      return imgMatch[1];
    }
    
    // Fallback to default image
    return "/images/default-project.webp";
  };

  // Transform WordPress API data to our project format
  const transformProjectData = (post) => {
    // Extract tags from categories or custom fields
    const tags = [];
    if (post._embedded && post._embedded['wp:term']) {
      post._embedded['wp:term'].forEach(termGroup => {
        termGroup.forEach(term => {
          if (term.taxonomy === 'post_tag' || term.taxonomy === 'category') {
            tags.push(term.name);
          }
        });
      });
    }

    // Extract service tags (you might need to adjust based on your WordPress setup)
    const serviceTags = [];
    if (tags.includes('Sarrdeh Care') || post.content?.rendered?.includes('Sarrdeh Care')) {
      serviceTags.push('Sarrdeh Care');
    }
    if (tags.includes('Sarrdeh Creative') || post.content?.rendered?.includes('Sarrdeh Creative')) {
      serviceTags.push('Sarrdeh Creative');
    }
    if (tags.includes('Sarrdeh Development') || post.content?.rendered?.includes('Sarrdeh Development')) {
      serviceTags.push('Sarrdeh Development');
    }
    if (tags.includes('Sarrdeh Marketing') || post.content?.rendered?.includes('Sarrdeh Marketing')) {
      serviceTags.push('Sarrdeh Marketing');
    }

    return {
      id: post.id,
      title: post.title?.rendered || post.title || 'Untitled Project',
      category: post._embedded?.['wp:term']?.[0]?.[0]?.name || 'General',
      service: serviceTags[0] || 'Sarrdeh Development',
      location: post.meta?._project_location || 'Global',
      description: post.excerpt?.rendered?.replace(/<[^>]*>/g, '') || post.content?.rendered?.replace(/<[^>]*>/g, '').substring(0, 200) + '...',
      image: extractImageUrl(post),
      tags: serviceTags.length > 0 ? serviceTags : ['Sarrdeh Development'],
      views: post.meta?._project_views || Math.floor(Math.random() * 1000) + 500,
      date: post.date,
      modified: post.modified,
      link: post.link,
      slug: post.slug
    };
  };

  // Fetch projects from WordPress API
  const fetchProjects = async () => {
    try {
      setLoading(true);
      setError(null);

      // Try multiple endpoints to find project data
      const endpoints = [
        API_ENDPOINTS.customPosts, // Try custom post type first
        API_ENDPOINTS.posts // Fallback to regular posts
      ];

      let projectsData = [];

      // Try each CORS proxy
      for (const proxy of CORS_PROXIES) {
        for (const endpoint of endpoints) {
          try {
            const url = proxy + endpoint;
            console.log(`Trying to fetch from: ${url}`);
            
            const response = await fetch(url, {
              method: 'GET',
              headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                // Add CORS headers for proxy requests
                ...(proxy && {
                  'Origin': window.location.origin,
                  'X-Requested-With': 'XMLHttpRequest'
                })
              },
              // Add timeout for requests
              signal: AbortSignal.timeout(10000) // 10 second timeout
            });

            if (response.ok) {
              const data = await response.json();
              console.log('API Response:', data);
              
              if (Array.isArray(data) && data.length > 0) {
                projectsData = data.map(transformProjectData);
                console.log('Transformed projects:', projectsData);
                break; // Use first successful endpoint
              }
            } else {
              console.log(`HTTP ${response.status}: ${response.statusText}`);
            }
          } catch (endpointError) {
            console.log(`Failed to fetch from ${endpoint} with proxy ${proxy}:`, endpointError.message);
            
            // If it's a CORS error, try next proxy
            if (endpointError.message.includes('CORS') || endpointError.message.includes('Failed to fetch')) {
              continue;
            }
            
            // For other errors, try next endpoint
            break;
          }
        }
        
        // If we got data, break out of proxy loop
        if (projectsData.length > 0) {
          break;
        }
      }

      // If no data from API, use sample data
      if (projectsData.length === 0) {
        console.log('No data from API, using sample data');
        setError('Unable to connect to WordPress API due to CORS restrictions. Using sample data.');
        projectsData = sampleProjects;
      }

      setProjects(projectsData);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching projects:', error);
      setError('Failed to load projects from API - using sample data instead');
      setProjects(sampleProjects); // Fallback to sample data
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const filteredProjects = projects.filter(project => {
    const categoryMatch = selectedCategory === 'All' || project.category === selectedCategory;
    const serviceMatch = selectedService === 'All' || project.tags.includes(selectedService);
    return categoryMatch && serviceMatch;
  });

  const sortedProjects = [...filteredProjects].sort((a, b) => {
    switch (sortBy) {
      case 'Most Viewed':
        return b.views - a.views;
      case 'Newest':
        return new Date(b.date) - new Date(a.date);
      case 'Oldest':
        return new Date(a.date) - new Date(b.date);
      default:
        return 0;
    }
  });

  const handleProjectClick = (project) => {
    if (project.link) {
      window.open(project.link, '_blank');
    }
  };

  return (
    <div style={{ backgroundColor: '#0a0a0a', minHeight: '100vh', color: 'white' }}>
      <Navbar />
      
      {/* Header Section */}
      <section style={{ 
        padding: '120px 20px 80px',
        background: 'linear-gradient(135deg, #0a2a2a 0%, #000000 100%)',
        position: 'relative',
        overflow: 'hidden'
      }}>
        {/* Background Effects */}
        <div style={{
          position: 'absolute',
          top: '10%',
          right: '10%',
          width: '200px',
          height: '200px',
          background: '#57B6B2',
          filter: 'blur(120px)',
          opacity: 0.3,
          zIndex: 0
        }} />
        
        <div style={{
          position: 'absolute',
          bottom: '20%',
          left: '5%',
          width: '150px',
          height: '150px',
          background: '#57B6B2',
          filter: 'blur(100px)',
          opacity: 0.2,
          zIndex: 0
        }} />

        <div className="container col-lg-6" style={{ position: 'relative', zIndex: 2 }}>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '3rem', marginBottom: '2rem', flexWrap: 'wrap' }}>
            <h1 style={{
              fontSize: '3.5rem',
              fontWeight: '700',
              background: 'linear-gradient(135deg, #ffffff 0%, #57b6b2 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              textAlign: 'center',
              margin: 0
            }}>
              Our Work
            </h1>

            <style>{`
              /* 1) Container */
              .hero-anim {
                position: relative;
                overflow: visible;
                text-align: center;
              }

              /* 2) Wrapper now hosts the neon bar/dot */
              .screen-wrapper {
                display: inline-block;
                position: relative;
              }

              /* 3) Initial neon BAR on the wrapper */
              .screen-wrapper::before {
                content: "";
                position: absolute;
                top: 0; left: 50%;
                transform: translateX(-50%);
                width: 300px;
                height: 0;
                border-top: 3px solid #57B6B2;
                transition:
                  width 0.3s ease,
                  height 0.3s ease,
                  border-radius 0.3s ease,
                  background-color 0.3s ease,
                  border-top-width 0.3s ease;
                z-index: 4;
                will-change: width, height, border-radius, background-color;
                filter:
                  drop-shadow(0 0.8px 0.8px #57B6B2)
                  drop-shadow(0 1.4px 1.4px #57B6B2)
                  drop-shadow(0 4.4px 4.4px #57B6B2)
                  drop-shadow(0 10.9px 10.9px #57B6B2)
                  drop-shadow(0 20.9px 20.9px #57B6B2)
                  drop-shadow(0 30.6px 30.6px #57B6B2);
              }

              /* 4) On hover: BAR → DOT (no overlay removal) */
              .screen-wrapper:hover::before {
                width: 0;
                height: 0;
                border-top-width: 0;
                border-radius: 50%;
                background-color: #57B6B2;
              }

              /* 5) Glass overlay stays entirely static */
              .screen-wrapper .glass-overlay {
                position: absolute;
                top: 0; left: 0;
                width: 100%; height: 100%;
                border-radius: 30px;
                border: 1px solid #ffffff55;
                box-shadow: 0 4px 16px rgba(87,182,178,0.05);
                background: rgba(255,255,255,0.08);
                z-index: 3;
              }

              /* 6) Logo bounce (unchanged) */
              .screen-wrapper .glass-logo {
                position: absolute;
                top: 50%; left: 50%;
                transform: translate(-50%, -50%);
                z-index: 5;
                animation: logo-bounce 3s ease-in-out infinite;
              }
              @keyframes logo-bounce {
                0%,100% { transform: translate(-50%, -50%) translateY(0); }
                50%     { transform: translate(-50%, -50%) translateY(-20px); }
              }

              /* 7) Icons blurred at rest (unchanged) */
              .screen-wrapper .icon {
                position: absolute;
               
                filter: blur(4px);
                z-index: 1;
                transition: transform 0.5s ease,
                            filter 0.5s ease,
                            z-index 0s 0.5s;
              }
              .screen-wrapper .icon-seo   { top:  5%; left: 30%; width:60px; height:48px; }
              .screen-wrapper .icon-figma { top: 70%; left: 80%; width:48px; height:57px;}
              .screen-wrapper .icon-dev   { top: 10%; left: 80%; width:51px; height:48px; }
              .screen-wrapper .icon-care   { top: 35%;
              left: 20%; width:56px; height:46px; }

              /* 8) Icons un-blur & fly on hover (unchanged) */
              .screen-wrapper:hover .icon {
                filter: blur(0);
                z-index: 6;              /* immediate on hover in */
                transition:
                  transform 0.5s ease,
                  filter    0.5s ease,
                  z-index   0s;          /* no delay on hover in */
              }
              .screen-wrapper:hover .icon-seo   { transform: translate(-50%, -70px);            }
              .screen-wrapper:hover .icon-figma { transform: translate(calc(-50% + 135px), 25px); }
              .screen-wrapper:hover .icon-dev   { transform: translate(calc(-50% + 135px), 5px);}
              .screen-wrapper:hover .icon-care   {    transform: translate(calc(-50% + 330px), -145px);}

              @media screen and (max-width: 768px) {
                .screen-wrapper .icon-seo {
                  width: 50px;
                  height: 40px;
                  top: 10%;
                  left: 25%;
                }

                .screen-wrapper .icon-figma {
                  width: 40px;
                  height: 50px;
                  top: 60%;
                  left: 70%;
                }

                .screen-wrapper .icon-dev {
                  width: 45px;
                  height: 40px;
                  top: 20%;
                  left: 70%;
                }

                .screen-wrapper .icon-care {
                  width: 50px;
                  height: 40px;
                  top: 50%;
                  left: 25%;
                }

                .screen-wrapper .glass-logo {
                  top: 50%;
                  /* slightly up for smaller screens */
                  left: 50%;
                  transform: translateX(-50%) translateY(-15%);
                  height: 200px;
                }

                .screen-wrapper:hover .icon-seo {
                  transform: translate(-50%, -70px); 
                }

                .screen-wrapper:hover .icon-figma {
                  transform: translate(calc(-50% + 60px), +85px);
                }

                .screen-wrapper:hover .icon-dev {
                  transform: translate(calc(-50% - 175px), 175px);
                }

                .screen-wrapper:hover .icon-care {
                  transform: translate(calc(-50% + 245px), -155px);
                }

                /* Adjusting neon bar for smaller screens */
                .screen-wrapper::before {
                  width: 150px;
                }
              }
            `}</style>

            <div className="screen-wrapper col-lg-6">
              <img
                className="screen"
                src="/wp-content/uploads/2025/06/portfolio-1.png"
                alt="Portfolio screens"
              />
              <div className="glass-overlay"></div>
              <img
                className="glass-logo"
                src="/wp-content/uploads/2025/06/Sarrdeh-glass-floating-logo.png"
                alt="Glass Logo"
              />
              <img
                className="icon icon-seo"
                src="/wp-content/uploads/2025/06/Frame-seo.png"
                alt="SEO Icon"
              />
              <img
                className="icon icon-figma"
                src="/wp-content/uploads/2025/06/design-frame.png"
                alt="Figma Icon"
              />
              <img
                className="icon icon-dev"
                src="/wp-content/uploads/2025/06/frame-code.png"
                alt="Dev Icon"
              />
              <img
                className="icon icon-care"
                src="/wp-content/uploads/2025/06/care-frame.png"
                alt="Care Icon"
              />
            </div>
          </div>

          <p style={{
            fontSize: '1.2rem',
            lineHeight: '1.8',
            textAlign: 'center',
            maxWidth: '800px',
            margin: '0 auto 3rem',
            opacity: 0.9,
            color: '#cccccc'
          }}>
            We love seeing the fruits of our hard work come to life as real results.
            These projects are selected examples showcasing how SarrdehTech has helped its partners achieve lasting success.
          </p>

          {/* Filter Icons */}
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '2rem',
            marginBottom: '3rem',
            flexWrap: 'wrap'
          }}>
            {['Portfolio screens', 'Glass Logo', 'SEO Icon', 'Figma Icon', 'Dev Icon'].map((icon, index) => (
              <div key={index} style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                padding: '0.5rem 1rem',
                borderRadius: '25px',
                border: '1px solid rgba(87, 182, 178, 0.3)',
                background: 'rgba(87, 182, 178, 0.1)',
                transition: 'all 0.3s ease',
                cursor: 'pointer'
              }}>
                <div style={{
                  width: '20px',
                  height: '20px',
                  background: '#57B6B2',
                  borderRadius: '50%'
                }} />
                <span style={{ fontSize: '0.9rem' }}>{icon}</span>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Filters Section */}
      <section style={{ 
        padding: '40px 20px',
        background: '#111111',
        borderBottom: '1px solid rgba(87, 182, 178, 0.2)'
      }}>
        <div className="container">
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            flexWrap: 'wrap',
            gap: '1rem'
          }}>
            {/* Service Filters */}
            <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
              {services.map(service => {
                const serviceColor = getServiceColor(service);
                const isSelected = selectedService === service;
                
                return (
                  <button
                    key={service}
                    onClick={() => setSelectedService(service)}
                    style={{
                      padding: '0.5rem 1.5rem',
                      background: isSelected ? serviceColor : 'transparent',
                      border: `1px solid ${isSelected ? serviceColor : `rgba(${service === 'Sarrdeh Marketing' ? '243, 39, 53' : service === 'Sarrdeh Creative' ? '255, 209, 0' : service === 'Sarrdeh Development' ? '87, 182, 178' : '30, 99, 144'}, 0.3)`}`,
                      borderRadius: '25px',
                      color: 'white',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      fontSize: '0.9rem',
                      boxShadow: isSelected ? `0 0 10px ${serviceColor}40` : 'none'
                    }}
                  >
                    {service}
                  </button>
                );
              })}
            </div>

            {/* Sort Options */}
            <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
              <span style={{ fontSize: '0.9rem', opacity: 0.7 }}>Sort by:</span>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                style={{
                  padding: '0.5rem 1rem',
                  background: 'transparent',
                  border: '1px solid rgba(87, 182, 178, 0.3)',
                  borderRadius: '8px',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                {sortOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
          </div>

          {/* Category Filters */}
          <div style={{ marginTop: '2rem' }}>
            <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
              {categories.map(category => (
                <button
                  key={category}
                  onClick={() => setSelectedCategory(category)}
                  style={{
                    padding: '0.4rem 1rem',
                    background: selectedCategory === category ? '#57B6B2' : 'transparent',
                    border: `1px solid ${selectedCategory === category ? '#57B6B2' : 'rgba(87, 182, 178, 0.2)'}`,
                    borderRadius: '20px',
                    color: 'white',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    fontSize: '0.8rem',
                    opacity: selectedCategory === category ? 1 : 0.7
                  }}
                >
                  {category}
                </button>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Projects Grid */}
      <section style={{ padding: '60px 20px' }}>
        <div className="container">
          {loading ? (
            <div style={{ textAlign: 'center', padding: '100px 0' }}>
              <div style={{
                width: '50px',
                height: '50px',
                border: '3px solid rgba(87, 182, 178, 0.3)',
                borderTop: '3px solid #57B6B2',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite',
                margin: '0 auto'
              }} />
              <p style={{ marginTop: '1rem', opacity: 0.7 }}>
                {error ? 'Loading fallback data...' : 'Loading projects from WordPress API...'}
              </p>
            </div>
          ) : (
            <>
              {error && (
                <div style={{ textAlign: 'center', marginBottom: '2rem', padding: '1rem', background: 'rgba(243, 39, 53, 0.1)', border: '1px solid rgba(243, 39, 53, 0.3)', borderRadius: '8px' }}>
                  <p style={{ color: '#F32735', margin: 0 }}>{error} - Using sample data</p>
                </div>
              )}
              
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fill, minmax(400px, 1fr))',
                gap: '2rem'
              }}>
                {sortedProjects.map((project, index) => (
                  <div 
                    key={project.id}
                    style={{
                      background: 'linear-gradient(135deg, #1a1a1a 0%, #0a0a0a 100%)',
                      borderRadius: '15px',
                      padding: '2rem',
                      border: '1px solid rgba(87, 182, 178, 0.1)',
                      transition: 'all 0.3s ease',
                      cursor: 'pointer',
                      position: 'relative',
                      overflow: 'hidden'
                    }}
                    onClick={() => handleProjectClick(project)}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.transform = 'translateY(-5px)';
                      e.currentTarget.style.boxShadow = '0 10px 30px rgba(87, 182, 178, 0.2)';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.transform = 'translateY(0)';
                      e.currentTarget.style.boxShadow = 'none';
                    }}
                  >
                    {/* Hover Effect */}
                    <div style={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: 'linear-gradient(135deg, rgba(87, 182, 178, 0.1) 0%, transparent 100%)',
                      opacity: 0,
                      transition: 'opacity 0.3s ease',
                      pointerEvents: 'none'
                    }} />

                    {/* Project Image */}
                    <div style={{
                      width: '100%',
                      height: '200px',
                      background: '#2a2a2a',
                      borderRadius: '10px',
                      marginBottom: '1.5rem',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      overflow: 'hidden'
                    }}>
                      <img
                        src={project.image}
                        alt={project.title}
                        style={{
                          maxWidth: '80%',
                          maxHeight: '80%',
                          objectFit: 'contain'
                        }}
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                      <div style={{
                        display: 'none',
                        alignItems: 'center',
                        justifyContent: 'center',
                        width: '100%',
                        height: '100%',
                        color: '#57B6B2',
                        fontSize: '2rem'
                      }}>
                        📁
                      </div>
                    </div>

                    {/* Project Info */}
                    <div>
                      <h3 style={{
                        fontSize: '1.3rem',
                        fontWeight: '600',
                        marginBottom: '0.5rem',
                        color: '#ffffff'
                      }}>
                        {project.title}
                      </h3>
                      
                      <p style={{
                        fontSize: '0.9rem',
                        color: '#57B6B2',
                        marginBottom: '0.5rem'
                      }}>
                        {project.category} • {project.location}
                      </p>

                      <div style={{
                        display: 'flex',
                        flexWrap: 'wrap',
                        gap: '0.5rem',
                        marginBottom: '1rem'
                      }}>
                        {project.tags.map(tag => {
                          const tagColor = getServiceColor(tag);
                          return (
                            <span key={tag} style={{
                              padding: '0.2rem 0.6rem',
                              background: `rgba(${tag === 'Sarrdeh Marketing' ? '243, 39, 53' : tag === 'Sarrdeh Creative' ? '255, 209, 0' : tag === 'Sarrdeh Development' ? '87, 182, 178' : '30, 99, 144'}, 0.1)`,
                              border: `1px solid rgba(${tag === 'Sarrdeh Marketing' ? '243, 39, 53' : tag === 'Sarrdeh Creative' ? '255, 209, 0' : tag === 'Sarrdeh Development' ? '87, 182, 178' : '30, 99, 144'}, 0.3)`,
                              borderRadius: '12px',
                              fontSize: '0.7rem',
                              color: tagColor
                            }}>
                              {tag}
                            </span>
                          );
                        })}
                      </div>

                      <p style={{
                        fontSize: '0.95rem',
                        lineHeight: '1.6',
                        color: '#cccccc',
                        marginBottom: '1.5rem',
                        display: '-webkit-box',
                        WebkitLineClamp: 3,
                        WebkitBoxOrient: 'vertical',
                        overflow: 'hidden'
                      }}>
                        {project.description}
                      </p>

                      <div style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center'
                      }}>
                        <span style={{
                          fontSize: '0.8rem',
                          color: '#888888'
                        }}>
                          {project.views} views
                        </span>
                        
                        <button style={{
                          padding: '0.5rem 1rem',
                          background: 'transparent',
                          border: '1px solid #57B6B2',
                          borderRadius: '8px',
                          color: '#57B6B2',
                          cursor: 'pointer',
                          transition: 'all 0.3s ease',
                          fontSize: '0.9rem'
                        }}>
                          View Project →
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}

          {!loading && sortedProjects.length === 0 && (
            <div style={{ textAlign: 'center', padding: '100px 0' }}>
              <p style={{ fontSize: '1.2rem', opacity: 0.7 }}>
                No projects found matching your criteria.
              </p>
            </div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <CTA 
        title="Do you have a project in the idea stage?"
        subtitle="Take advantage of our free consultation to develop your idea, and let our team help you map out a plan and turn it into a successful real-world project."
        buttonText="Book a Free Consultation"
        buttonLink="/contact-us"
      />

      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}
