import React from 'react';
import StarBorder from './Animations/StarBorder/StarBorder.jsx';

const CTA = ({ 
  title = "You have a project in the idea stage?",
  subtitle = "Let's discuss your ideas anTake advantage of our free consultation to develop your idea, and let our team help you map out a plan and turn your concept into a successful, real-world project.d bring them to life",
  buttonText = "Book Your Free Consultation",
  buttonLink = "/contact-us",
  backgroundImage = "/images/cta-man.png", // CTA background image
  className = ""
}) => {
  return (
    <section className={`cta-section py-5 ${className}`} style={{ position: 'relative' }}>
      {/* Background Image */}
      <div 
        className="cta-background"
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          zIndex: 0
        }}
      />
      
      {/* Overlay for better text readability */}
      <div 
        className="cta-overlay"
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
          zIndex: 1
        }}
      />

      {/* Content */}
      <div className="container" style={{ position: 'relative', zIndex: 2 }}>
        <div className="row justify-content-center align-items-center text-center">
          <div className="col-lg-8 col-md-10">
            <h2 className="cta-title mb-4" style={{ 
              color: '#fff', 
              fontSize: '2.5rem', 
              fontWeight: '700',
              lineHeight: '1.2'
            }}>
              {title}
            </h2>
            
            <p className="cta-subtitle mb-5" style={{ 
              color: '#F7F9F9', 
              fontSize: '1.2rem',
              lineHeight: '1.6',
              marginBottom: '2rem'
            }}>
              {subtitle}
            </p>
            
            <div className="cta-button-container">
              <StarBorder 
                href={buttonLink}
                color='#57b6b2' 
                speed='5s'
                style={{
                  textDecoration: 'none',
                  display: 'inline-block'
                }}
              >
                {buttonText}
              </StarBorder>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default CTA;
