import React, { useMemo } from 'react';

export default function LogoSlider({
  logos,
  slideWidth = 150,      // px per logo
  durationFactor = 1,    // seconds per full loop (tweak for speed)
}) {
  // Duplicate the logos array so the track can loop seamlessly
  const slides = useMemo(() => [...logos, ...logos], [logos]);

  // Total width of the track in px
  const trackWidth = slides.length * slideWidth;

  // Animation duration: one full cycle across the original set
  const duration = (logos.length * slideWidth) / slideWidth * durationFactor;

  return (
    <div className="slider-home">
      <div
        className="slide-track"
        style={{
          width: `${trackWidth}px`,
          animation: `scroll ${duration}s linear infinite`,
        }}
      >
        {slides.map((url, idx) => (
          <div
            key={idx}
            className="slide"
            style={{ flex: `0 0 ${slideWidth}px` }}
          >
            <img src={url} alt="" />
          </div>
        ))}
      </div>
    </div>
  );
}
