import React, { useState, useEffect, useRef } from 'react';

export default function LogoSlider({
  logos,
  slideWidth = 150,       // width of one slide in px
  visibleCount = 5,       // how many logos are visible at once
  interval = 2000,        // ms between slides
  transitionMs = 500      // ms duration of slide transition
}) {
  const [index, setIndex] = useState(0);
  const trackRef = useRef();

  // advance the slide every `interval` milliseconds
  useEffect(() => {
    const id = setInterval(() => {
      setIndex(i => (i + 1) % logos.length);
    }, interval);
    return () => clearInterval(id);
  }, [logos.length, interval]);

  // adjust the track’s transform whenever index changes
  const trackStyle = {
    transform: `translateX(-${index * slideWidth}px)`,
    transition: `transform ${transitionMs}ms ease`
  };

  // compute container max-width
  const containerStyle = {
    width: `${slideWidth * visibleCount}px`,
    overflow: 'hidden'
  };

  return (
    <div className="slider-home" style={containerStyle}>
      <div className="slide-track" ref={trackRef} style={trackStyle}>
        {logos.map((url, idx) => (
          <div
            key={idx}
            className="slide"
            style={{ flex: `0 0 ${slideWidth}px` }}
          >
            <img src={url} alt="" />
          </div>
        ))}
      </div>
    </div>
  );
}
