import React from 'react';


export default function Hero() {
  return (
    <section className="hero services-section">
      <div className="container text-center position-relative" style={{ zIndex: 2 }}>
        <h1 className="display-4 fw-bold">
          في سردة تِك، تأتي مهمتنا بتقديم حلول متكاملة في التسويق الرقمي وتطوير المواقع
        </h1>
        <p className="lead mb-4">
          لتساعدك على الظهور، التميّز، وتحقيق نتائج ورؤى واضحة وملموسة.
        </p>
        <a href="/contact-us" className="btn btn-primary btn-lg">
          احجز استشارتك المجانية
        </a>
      </div>
      {/* semi-transparent overlay */}
      <div
        className="position-absolute top-0 start-0 w-100 h-100"
        style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}
      />
    </section>
  );
}
